site.headStyle:
.product-panel {
   display: flex;
   border: 1px solid #444;
   padding: 20px;
   background-color: #2C2C2C; /* Dark background */
   border-radius: 8px;
   width: 500px;
   margin: 20px auto;
   box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3); /* Darker shadow */
}

.product-image img {
   width: 150px;
   height: 150px;
   object-fit: cover;
   border-radius: 8px;
   margin-right: 20px;
}

.product-details {
   display: flex;
   flex-direction: column;
   justify-content: space-between;
}

.product-name {
   font-size: 24px;
   color: #FFFFFF; /* White text */
   margin: 0;
}

.product-description {
   font-size: 16px;
   color: #CCCCCC; /* Light gray for readability */
   margin: 10px 0;
}

.product-items,
.product-expiry,
.product-manufacture {
   font-size: 14px;
   color: #BBBBBB; /* Slightly lighter gray for extra details */
   margin: 5px 0;
}

.details-button {
   padding: 10px 20px;
   background-color: #3498db; /* Blue button */
   color: white;
   border: none;
   border-radius: 4px;
   cursor: pointer;
   transition: background-color 0.3s ease;
}

.details-button:hover {
   background-color: #2980b9; /* Darker blue on hover */
}

site.headScript:
// Example JSON data (an array of product objects)
const products = [
    {
        "id": 102,
        "barCode": "example",
        "name": "TestMaterial 3",
        "price": 6050,
        "url": "https://www.jiomart.com/p/groceries/fortune-chakki-fresh-whole-wheat-atta-10-kg/491417436",
        "imageUrl": "https://www.jiomart.com/images/product/original/491417436/fortune-chakki-fresh-whole-wheat-atta-10-kg-product-images-o491417436-p491417436-0-202203170527.jpg?im=Resize=(420,420)",
        "stock": 0,
        "description": "This is product",
        "status": true
    }
];

// Function to create product div
function createProductDiv(product) {
    const productPanel = document.createElement('div');
    productPanel.classList.add('product-panel');

    const productImageDiv = document.createElement('div');
    productImageDiv.classList.add('product-image');
    
    const productImage = document.createElement('img');
    productImage.src = product.imageUrl;
    productImage.alt = product.name;
    productImageDiv.appendChild(productImage);

    const productDetailsDiv = document.createElement('div');
    productDetailsDiv.classList.add('product-details');
    
    const productName = document.createElement('h2');
    productName.classList.add('product-name');
    productName.textContent = product.name;
    
    const productDescription = document.createElement('p');
    productDescription.classList.add('product-description');
    productDescription.textContent = product.description;
    
    const productPrice = document.createElement('p');
    productPrice.classList.add('product-price');
    productPrice.textContent = `Price: $${product.price}`;

    const productStock = document.createElement('p');
    productStock.classList.add('product-stock');
    productStock.textContent = `In Stock: ${product.stock}`;

    const productStatus = document.createElement('p');
    productStatus.classList.add('product-status');
    productStatus.textContent = `Status: ${product.status ? 'Available' : 'Unavailable'}`;

    const productLink = document.createElement('a');
    productLink.href = product.url;
    productLink.textContent = "View Product";
    productLink.classList.add('product-link');

    const detailsButton = document.createElement('button');
    detailsButton.classList.add('details-button');
    detailsButton.textContent = "Show More Details";

    // Append all elements to the details div
    productDetailsDiv.appendChild(productName);
    productDetailsDiv.appendChild(productDescription);
    productDetailsDiv.appendChild(productPrice);
    productDetailsDiv.appendChild(productStock);
    productDetailsDiv.appendChild(productStatus);
    productDetailsDiv.appendChild(productLink);
    productDetailsDiv.appendChild(detailsButton);

    // Append image and details to the main product panel
    productPanel.appendChild(productImageDiv);
    productPanel.appendChild(productDetailsDiv);

    return productPanel;
}

// Get the product container and add product divs
const productContainer = document.getElementById('product-container');

// Loop through each product in the JSON array and append to the container
products.forEach(product => {
    const productDiv = createProductDiv(product);
    productContainer.appendChild(productDiv);
});
